INSERT INTO `wp_posts` VALUES(236, 3, '2013-05-13 18:44:44', '2013-05-13 18:44:44', 'Northeastern University celebrated its 111th commencement at TD Garden in Boston. More than 3,200 graduating seniors received diplomas. Jim Yong Kim, World Bank Group president, delivered the commencement address to a crowd of about 20,000. Honorary degrees were presented to: Jack D. Bryant, president of Bryant Associates; Barbara Lynch, CEO of Barbara Lynch Gruppo; and the first responders and law enforcement who selflessly sprung into action following the Boston Marathon bombings. Boston Police Commissioner Edward F. Davis III represented the first responders and law enforcement to accept the honorary degree at the ceremony.\r\n\r\n[gallery type="slideshow" link="none" ids="237,238,239,240,241,242,243,244"]', 'Commencement 2013', 'Northeastern University celebrated its 111th commencement at TD Garden in Boston. More than 3,200 graduating seniors received diplomas. Jim Yong Kim, World Bank President, delivered the commencement address to a crowd of about 20,000.\r\n', 'publish', 'open', 'open', '', 'commencement-2013', '', '', '2015-03-12 18:46:44', '2015-03-12 18:46:44', '', 0, 'http://localhost:8888/president/?p=236', 0, 'post', '', 0);
