INSERT INTO `wp_posts` VALUES(260, 3, '2015-03-12 18:51:41', '2015-03-12 18:51:41', 'November 27, 2012 - President Joseph E. Aoun hosts a forum, entitled "Innovation Imperative: The Future of Higher Eduction" at the Brookings Institution in Washington, DC.  The event marked the release of a national opinion poll to shed new light on Americans’ attitudes toward the future of higher education in the United States.  Photo by Paul Morigi.', 'Brookings Institution Higher Ed Forum', '', 'inherit', 'open', 'open', '', 'brookings-institution-higher-ed-forum', '', '', '2015-03-12 18:51:41', '2015-03-12 18:51:41', '', 259, 'http://localhost:8888/president/wp-content/uploads/2015/03/neu116221_highres.jpg', 0, 'attachment', 'image/jpeg', 0);
