INSERT INTO `wp_posts` VALUES(452, 4, '2011-05-05 16:55:54', '2011-05-05 16:55:54', '<i></i>Todd Smith, otherwise known as L.L. Cool J, takes part in a luncheon given in honor of the 2011 Torch Scholars at Northeastern University.\r\n\r\n[gallery type="slideshow" link="none" ids="453,454,455,456,457,458,459,460,461,462"]\r\n\r\n<a href="http://www.northeastern.edu/news/2011/05/torch_scholars/" target="_blank"><strong>News @ Northeastern Story</strong></a>', 'Torch Scholars Luncheon', 'Students in the inaugural class of Torch Scholars were honored at a pre-graduation luncheon where family, friends, and Northeastern staff affiliated with the Torch program celebrated their impressive accomplishments. Actor, musician, and philanthropist, Todd Smith, aka LL Cool J, was the keynote speaker at the luncheon where he imparted words of inspiration and personal experience to guide the students as they set off into the real world. Photos by Mary Knox Merrill', 'publish', 'open', 'open', '', 'torch-scholars-luncheon', '', '', '2015-03-16 16:18:10', '2015-03-16 16:18:10', '', 0, 'http://localhost:8888/president/?p=452', 0, 'post', '', 0);
