INSERT INTO `wp_posts` VALUES(581, 4, '2014-04-15 17:23:24', '2014-04-15 17:23:24', 'Today, as we mark the one-year anniversary of the day Boston – and all of us —were brought together in tragedy, may we also reflect on the collective support, individual perseverance and enormous strength of our community that have enabled us to move forward.\r\n\r\nhttps://www.youtube.com/watch?v=eVRmDCYB9yo', 'A Message from President Aoun', '', 'publish', 'open', 'open', '', 'a-message-from-president-aoun', '', '', '2015-03-17 17:24:22', '2015-03-17 17:24:22', '', 0, 'http://localhost:8888/president/?p=581', 0, 'post', '', 0);
