INSERT INTO `wp_posts` VALUES(644, 4, '2011-09-07 19:03:02', '2011-09-07 19:03:02', 'Please join me on Friday, September 9 at 10:00 am as the Northeastern community comes together to commemorate the 10th anniversary of the events of September 11, 2001.\r\n\r\nWe will gather in the John D. O’Bryant African-American Institute’s Cabral Center for a program of music and remembrance. As we honor those we have lost, we also will reflect on the resilience of our university community and our collective efforts to build a safer and more peaceful world.\r\n\r\nI hope to see you on September 9.\r\nJoseph E. Aoun\r\nPresident', '9/11 Remembrance', '', 'publish', 'open', 'open', '', '911-remembrance', '', '', '2015-03-17 19:03:37', '2015-03-17 19:03:37', '', 0, 'http://localhost:8888/president/?p=644', 0, 'post', '', 0);
