INSERT INTO `wp_posts` VALUES(709, 4, '2008-12-02 15:50:45', '2008-12-02 15:50:45', 'As a university community we feel both shock and grief about last week’s terrorist attacks in Mumbai.\r\n\r\nWe are reminded that we are an international community and that members of our Northeastern family are directly affected by this tragedy in their homeland.\r\n\r\nNortheastern offers our thoughts and prayers for the victims and survivors, along with our hopes that our children and our children’s children live in a safer, more secure world.\r\n\r\nJoseph E. Aoun,\r\n\r\nPresident', 'The tragedy in Mumbai', '', 'publish', 'open', 'open', '', 'the-tragedy-in-mumbai', '', '', '2015-03-18 15:51:15', '2015-03-18 15:51:15', '', 0, 'http://localhost:8888/president/?p=709', 0, 'post', '', 0);
